import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Attendance() {
  const [attendanceRecords, setAttendanceRecords] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAllAttendance = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/attendance/all');
        setAttendanceRecords(response.data);
      } catch (err) {
        console.error(err);
        setError('Failed to fetch attendance data.');
      } finally {
        setLoading(false);
      }
    };

    fetchAllAttendance();
  }, []);

  if (loading) return <div>Loading attendance records...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>All Attendance Records</h2>
      {attendanceRecords.length === 0 ? (
        <p>No attendance records found.</p>
      ) : (
        <table style={tableStyle}>
          <thead>
            <tr>
              <th style={thStyle}>ID</th>
              <th style={thStyle}>Employee ID</th>
              <th style={thStyle}>Employee Name</th>
              <th style={thStyle}>Type</th>
              <th style={thStyle}>Note</th>
              <th style={thStyle}>Timestamp</th>
            </tr>
          </thead>
          <tbody>
            {attendanceRecords.map((record) => (
              <tr key={record.id}>
                <td style={tdStyle}>{record.id}</td>
                <td style={tdStyle}>{record.employeeId}</td>
                <td style={tdStyle}>{record.employeeName}</td>
                <td style={tdStyle}>{record.type}</td>
                <td style={tdStyle}>{record.note || '-'}</td>
                <td style={tdStyle}>{new Date(record.timestamp).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

// Simple table styling
const tableStyle = {
  width: '100%',
  borderCollapse: 'collapse',
  marginTop: '20px'
};

const thStyle = {
  border: '1px solid #ddd',
  padding: '8px',
  backgroundColor: '#f4f4f4',
  textAlign: 'left'
};

const tdStyle = {
  border: '1px solid #ddd',
  padding: '8px'
};

export default Attendance;
