import React from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Dashboard.css'; // Import the CSS file

// Sample data for the employees table
const employees = [
    { id: '001', name: 'John Doe', email: 'john.doe@example.com', role: 'Developer' },
    { id: '002', name: 'Jane Smith', email: 'jane.smith@example.com', role: 'Designer' },
    { id: '003', name: 'Sam Wilson', email: 'sam.wilson@example.com', role: 'Project Manager' },
    { id: '004', name: 'Alice Johnson', email: 'alice.j@example.com', role: 'QA Tester' },
];

function Dashboard() {
    const navigate = useNavigate();

    const handleLogout = async () => {
        try {
            await axios.post('http://localhost:8080/api/auth/logout', {}, { withCredentials: true });
            navigate('/login');
        } catch (error) {
            console.error('Logout failed:', error);
        }
    };

    return (
        <div className="dashboard-container">
            {/* Sidebar */}
            <aside className="sidebar">
                <h1 className="logo">HRMS</h1>
                <nav className="sidebar-nav">
                    <ul>
                        <li>
                            <a href="#employees" className="nav-item active">
                                Employees
                            </a>
                            <ul className="sub-nav">
                                <li>
                                    <a href="#new-employees" className="nav-sub-item">
                                        New Employees
                                    </a>
                                </li>
                            </ul>
                        </li>
                    </ul>
                </nav>
            </aside>

            {/* Main Content */}
            <main className="main-content">
                {/* Header */}
                <header className="header">
                    <h2 className="header-title">Welcome to HR Management System</h2>
                    <button onClick={handleLogout} className="logout-button">
                        Logout
                    </button>
                </header>

                {/* Content Area */}
                <div className="content-area">
                    <h3 className="content-title">New Employees</h3>
                    <div className="table-container">
                        <table className="employee-table">
                            <thead>
                            <tr>
                                <th>Employee ID</th>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Role</th>
                            </tr>
                            </thead>
                            <tbody>
                            {employees.map((employee) => (
                                <tr key={employee.id}>
                                    <td>{employee.id}</td>
                                    <td>{employee.name}</td>
                                    <td>{employee.email}</td>
                                    <td>{employee.role}</td>
                                </tr>
                            ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </main>
        </div>
    );
}

export default Dashboard;
