import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function AdminLeave() {
  const [employees, setEmployees] = useState([]);
  const [selectedEmployee, setSelectedEmployee] = useState('');
  const [totalLeave, setTotalLeave] = useState('');
  const [remainingLeave, setRemainingLeave] = useState('');
  const [loading, setLoading] = useState(true);
  const [updating, setUpdating] = useState(false);

  // Fetch employees from backend
  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/employees/getall');

        // Make sure response is always an array
        const data = Array.isArray(response.data) ? response.data : [];
        setEmployees(data);
      } catch (err) {
        console.error('Failed to fetch employees', err);
        setEmployees([]);
      } finally {
        setLoading(false);
      }
    };

    fetchEmployees();
  }, []);

  // When an employee is selected, fetch their leave details
  const handleSelect = async (employeeId) => {
    setSelectedEmployee(employeeId);
    if (!employeeId) {
      setTotalLeave('');
      setRemainingLeave('');
      return;
    }

    try {
      const res = await axios.get(`http://localhost:8080/api/leave?employeeId=${employeeId}`);
      if (res.data) {
        setTotalLeave(res.data.totalLeave || '');
        setRemainingLeave(res.data.remainingLeave || '');
      } else {
        setTotalLeave('');
        setRemainingLeave('');
      }
    } catch (err) {
      console.error('Failed to fetch leave data', err);
      setTotalLeave('');
      setRemainingLeave('');
    }
  };

  // Update leave for the selected employee
  const handleUpdate = async () => {
    if (!selectedEmployee) return alert('Please select an employee!');
    if (totalLeave === '' || remainingLeave === '') return alert('Enter valid leave numbers!');

    setUpdating(true);
    try {
      await axios.post('http://localhost:8080/api/leave/update', {
        employeeId: selectedEmployee,
        totalLeave: parseInt(totalLeave, 10),
        remainingLeave: parseInt(remainingLeave, 10),
      });
      alert('Leave updated successfully!');
    } catch (err) {
      console.error('Update failed', err);
      alert('Failed to update leave.');
    } finally {
      setUpdating(false);
    }
  };

  if (loading) return <div style={styles.status}>Loading employees...</div>;

  if (employees.length === 0)
    return <div style={styles.status}>No employees found.</div>;

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Admin Leave Management</h2>

      <div style={styles.section}>
        <label style={styles.label}>Select Employee:</label>
        <select
          style={styles.select}
          value={selectedEmployee}
          onChange={(e) => handleSelect(e.target.value)}
        >
          <option value="">--Select--</option>
          {employees.map(emp => (
            <option key={emp.id} value={emp.id}>
              {emp.name} ({emp.id})
            </option>
          ))}
        </select>
      </div>

      {selectedEmployee && (
        <div style={styles.section}>
          <div style={styles.inputGroup}>
            <label style={styles.label}>Total Leave:</label>
            <input
              type="number"
              style={styles.input}
              value={totalLeave}
              onChange={(e) => setTotalLeave(e.target.value)}
            />
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.label}>Remaining Leave:</label>
            <input
              type="number"
              style={styles.input}
              value={remainingLeave}
              onChange={(e) => setRemainingLeave(e.target.value)}
            />
          </div>

          <button
            style={{ ...styles.button, opacity: updating ? 0.6 : 1 }}
            onClick={handleUpdate}
            disabled={updating}
          >
            {updating ? 'Updating...' : 'Update Leave'}
          </button>
        </div>
      )}
    </div>
  );
}

// Styling
const styles = {
  container: {
    padding: 30,
    background: 'linear-gradient(135deg, #0a0f1c, #1a202c)',
    minHeight: '100vh',
    color: '#f7fafc',
    fontFamily: 'Inter, sans-serif',
  },
  title: {
    fontSize: 28,
    fontWeight: '700',
    marginBottom: 30,
    background: 'linear-gradient(135deg, #60a5fa, #a78bfa)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
  },
  section: {
    marginBottom: 25,
  },
  label: {
    display: 'block',
    marginBottom: 8,
    fontWeight: 600,
    color: '#93c5fd',
  },
  select: {
    width: '100%',
    padding: '10px 15px',
    borderRadius: 10,
    border: '1px solid #2563eb',
    background: '#1e3a8a',
    color: 'white',
    fontSize: 16,
  },
  inputGroup: {
    marginBottom: 15,
  },
  input: {
    width: '100%',
    padding: '10px 15px',
    borderRadius: 10,
    border: '1px solid #2563eb',
    fontSize: 16,
    background: '#1e3a8a',
    color: 'white',
  },
  button: {
    padding: '12px 25px',
    backgroundColor: '#2563eb',
    color: 'white',
    border: 'none',
    borderRadius: 12,
    fontSize: 16,
    fontWeight: 600,
    cursor: 'pointer',
    transition: 'all 0.3s ease',
  },
  status: {
    padding: 20,
    fontSize: 18,
    textAlign: 'center',
  },
};
