import React, { useState, useEffect } from 'react'; // 1. Import useState and useEffect
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Dashboard.css';

function Dashboard() {
    // 2. State to store the list of employees from the backend
    const [employees, setEmployees] = useState([]);
    const navigate = useNavigate();

    // 3. Fetch data when the component mounts
    useEffect(() => {
        // Define an async function to get the data
        const fetchEmployees = async () => {
            try {
                const response = await axios.get('http://localhost:8080/getall');
                setEmployees(response.data); // Update the state with the fetched data
            } catch (error) {
                console.error("There was an error fetching the employee data:", error);
            }
        };

        fetchEmployees(); // Call the function to execute it
    }, []); // The empty array [] ensures this effect runs only once after the initial render

    const handleLogout = async () => {
        try {
            await axios.post('http://localhost:8080/api/auth/logout', {}, { withCredentials: true });
            navigate('/login');
        } catch (error) {
            console.error('Logout failed:', error);
        }
    };

    return (
        <div className="dashboard-container">
            <aside className="sidebar">
                <h1 className="logo">HRMS</h1>
                <nav className="sidebar-nav">
                    <ul>
                        <li>
                            <a href="#employees" className="nav-item active">Employees</a>
                            <ul className="sub-nav">
                                <li>
                                    <a href="#new-employees" className="nav-sub-item">New Employees</a>
                                </li>
                            </ul>
                        </li>
                    </ul>
                </nav>
            </aside>

            <main className="main-content">
                <header className="header">
                    <h2 className="header-title">Welcome to HR Management System</h2>
                    <button onClick={handleLogout} className="logout-button">Logout</button>
                </header>

                <div className="content-area">
                    <h3 className="content-title">New Employees</h3>
                    <div className="table-container">
                        <table className="employee-table">
                            <thead>
                            <tr>
                                {/* 4. Updated table headers to match your Employee model */}
                                <th>Employee ID</th>
                                <th>Name</th>
                                <th>Email</th>
                                <th>NIC</th>
                                <th>Gender</th>
                            </tr>
                            </thead>
                            <tbody>
                            {/* 5. Map over the 'employees' state variable to render the data */}
                            {employees.map((employee) => (
                                <tr key={employee.id}>
                                    <td>{employee.id}</td>
                                    <td>{employee.name}</td>
                                    <td>{employee.email}</td>
                                    <td>{employee.nic}</td>
                                    <td>{employee.gender}</td>
                                </tr>
                            ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </main>
        </div>
    );
}

export default Dashboard;
