import React, { useState } from 'react';
import axios from 'axios'; // Import axios
import './login.css';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(''); // State to hold login error messages

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(''); // Reset error message on new attempt

        // Spring Security's formLogin expects a FormData format
        const params = new URLSearchParams();
        params.append('username', email); // IMPORTANT: Spring Security expects 'username' by default
        params.append('password', password);

        try {
            // The URL of your Spring Boot login endpoint
            const response = await axios.post('http://localhost:8080/api/auth/login', params, {
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded'
                },
                withCredentials: true // Crucial for sending session cookies
            });

            if (response.status === 200) {
                console.log('Login successful!');
                alert('Login Successful!');
                // Here you would typically redirect to the admin dashboard
                // window.location.href = '/admin/dashboard';
            }
        } catch (err) {
            console.error('Login failed:', err);
            setError('Invalid email or password. Please try again.');
        }
    };

    return (
        <div className="min-h-screen bg-gray-900 flex items-center justify-center">
            <div className="w-full max-w-md bg-gray-800 rounded-lg shadow-xl p-8">
                <h2 className="text-2xl font-bold text-white text-center mb-6">Admin Panel Login</h2>

                {/* Display error message if it exists */}
                {error && <p className="text-red-500 text-center mb-4">{error}</p>}

                <form onSubmit={handleSubmit} className="space-y-6">
                    <div>
                        <label htmlFor="email" className="block text-sm font-medium text-gray-300">
                            Email
                        </label>
                        <input
                            id="email"
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                            className="mt-1 w-full px-4 py-2 bg-gray-700 text-white border border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                            placeholder="Enter your email"
                        />
                    </div>
                    <div>
                        <label htmlFor="password" className="block text-sm font-medium text-gray-300">
                            Password
                        </label>
                        <input
                            id="password"
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            className="mt-1 w-full px-4 py-2 bg-gray-700 text-white border border-gray-600 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                            placeholder="Enter your password"
                        />
                    </div>
                    <button
                        type="submit"
                        className="w-full py-2 px-4 bg-blue-600 text-white font-semibold rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-200"
                    >
                        Sign In
                    </button>
                </form>
            </div>
        </div>
    );
}

export default Login;
