import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function AdminLeave() {
  const [employees, setEmployees] = useState([]);
  const [selectedEmployee, setSelectedEmployee] = useState('');
  const [totalLeave, setTotalLeave] = useState('');
  const [remainingLeave, setRemainingLeave] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/employees/getall');
        setEmployees(response.data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchEmployees();
  }, []);

  const handleSelect = async (employeeId) => {
    setSelectedEmployee(employeeId);
    try {
      const res = await axios.get(`http://localhost:8080/api/leave?employeeId=${employeeId}`);
      if (res.data) {
        setTotalLeave(res.data.totalLeave);
        setRemainingLeave(res.data.remainingLeave);
      } else {
        setTotalLeave('');
        setRemainingLeave('');
      }
    } catch {
      setTotalLeave('');
      setRemainingLeave('');
    }
  };

  const handleUpdate = async () => {
    if (!selectedEmployee) return alert('Select an employee!');
    try {
      await axios.post('http://localhost:8080/api/leave/update', {
        employeeId: selectedEmployee,
        totalLeave: parseInt(totalLeave),
        remainingLeave: parseInt(remainingLeave),
      });
      alert('Leave updated successfully!');
    } catch {
      alert('Update failed!');
    }
  };

  if (loading) return <div>Loading employees...</div>;

  return (
    <div style={{ padding: 20 }}>
      <h2>Admin Leave Management</h2>

      <div style={{ marginBottom: 20 }}>
        <label>Select Employee: </label>
        <select
          value={selectedEmployee}
          onChange={(e) => handleSelect(e.target.value)}
        >
          <option value="">--Select--</option>
          {employees.map(emp => (
            <option key={emp.id} value={emp.id}>
              {emp.name} ({emp.employeeId})
            </option>
          ))}
        </select>
      </div>

      {selectedEmployee && (
        <div>
          <div style={{ marginBottom: 10 }}>
            <label>Total Leave: </label>
            <input
              type="number"
              value={totalLeave}
              onChange={(e) => setTotalLeave(e.target.value)}
            />
          </div>

          <div style={{ marginBottom: 10 }}>
            <label>Remaining Leave: </label>
            <input
              type="number"
              value={remainingLeave}
              onChange={(e) => setRemainingLeave(e.target.value)}
            />
          </div>

          <button onClick={handleUpdate} style={buttonStyle}>
            Update Leave
          </button>
        </div>
      )}
    </div>
  );
}

const buttonStyle = {
  padding: '10px 20px',
  backgroundColor: '#1e3a8a',
  color: 'white',
  border: 'none',
  borderRadius: 8,
  cursor: 'pointer',
};
