import React, { useState, useEffect, useCallback } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Alert,
  ActivityIndicator,
  Platform,
  FlatList,
  SafeAreaView,
  RefreshControl, // Import RefreshControl for pull-to-refresh
} from 'react-native';
import axios from 'axios';
import AsyncStorage from '@react-native-async-storage/async-storage';

// API URL for the Android Emulator
const API_URL = Platform.OS === 'ios' ? 'http://localhost:8080' : 'http://10.0.2.2:8080';

// Helper function to format the timestamp from your backend
const formatTimestamp = (timestamp) => {
  if (!timestamp) return { date: 'N/A', time: 'N/A' };
  try {
    const dateObj = new Date(timestamp);
    return {
      date: dateObj.toLocaleDateString(),
      time: dateObj.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
    };
  } catch (error) {
    console.error('Invalid timestamp:', timestamp);
    return { date: 'Invalid Date', time: '' };
  }
};

const Attendance = () => {
  const [history, setHistory] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [employeeId, setEmployeeId] = useState(null);
  const [employeeName, setEmployeeName] = useState(null);
  const [error, setError] = useState(null);

  // --- 1. Load Employee Data and Fetch History ---
  const loadData = useCallback(async () => {
    try {
      const id = await AsyncStorage.getItem('@employee_id');
      const name = await AsyncStorage.getItem('@employee_name'); // Assuming you save this at login

      if (id && name) {
        setEmployeeId(id);
        setEmployeeName(name);
        await fetchHistory(id); // Fetch history after getting ID
      } else {
        setError('Could not find employee data. Please log in again.');
        Alert.alert('Error', 'Could not find employee data. Please log in again.');
      }
    } catch (e) {
      console.error('Failed to load data from storage', e);
      setError('Failed to load local data.');
    } finally {
      setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    loadData();
  }, [loadData]);

  // --- 2. Fetch History from Backend ---
  const fetchHistory = async (id) => {
    if (!id) return;
    try {
      const response = await axios.get(`${API_URL}/api/attendance`, {
        params: { employeeId: id },
      });
      // Sort by timestamp, newest first
      const sortedData = response.data.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));
      setHistory(sortedData);
      setError(null);
    } catch (err) {
      console.error('Failed to fetch attendance history', err);
      setError('Could not load attendance history.');
    }
  };

  // --- 3. Handle Check-In / Check-Out ---
  const handleAttendance = async (type) => {
    if (!employeeId || !employeeName) {
      Alert.alert('Error', 'Employee data not loaded.');
      return;
    }

    // Check if user is already checked in/out
    const lastEvent = history[0];
    if (lastEvent && lastEvent.type === type) {
      Alert.alert('Info', `You have already
      ${type.toLowerCase()}
      .`);
      return;
    }

    try {
      // Use the POST endpoint from your controller
      await axios.post(`${API_URL}/api/attendance`, {
        employeeId: employeeId,
        employeeName: employeeName,
        type: type, // "CHECKIN" or "CHECKOUT"
        note: '', // Optional note
      });

      Alert.alert('Success', `You have successfully
      ${type.toLowerCase()}
      .`);
      await fetchHistory(employeeId); // Refresh the list
    } catch (err) {
      console.error(`Failed to ${type}`, err);
      Alert.alert('Error', `Failed to record your ${type.toLowerCase()}.`);
    }
  };

  // --- 4. Handle Pull-to-Refresh ---
  const onRefresh = useCallback(async () => {
    setIsRefreshing(true);
    await fetchHistory(employeeId);
    setIsRefreshing(false);
  }, [employeeId]);

  // --- 5. Render Functions ---

  // Renders the Check-In/Out buttons
  const renderHeader = () => (
    <View>
      <Text style={styles.title}>Attendance</Text>
      <Text style={styles.subtitle}>Tap to clock in or out for the day</Text>
      <View style={styles.buttonContainer}>
        <TouchableOpacity
          style={[styles.button, styles.checkInButton]}
          onPress={() => handleAttendance('CHECKIN')}>
          <Text style={styles.buttonText}>✓ Check In</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[styles.button, styles.checkOutButton]}
          onPress={() => handleAttendance('CHECKOUT')}>
          <Text style={styles.buttonText}>✗ Check Out</Text>
        </TouchableOpacity>
      </View>
      <Text style={styles.historyTitle}>History</Text>
    </View>
  );

  // Renders a single attendance record
  const renderItem = ({ item }) => {
    const { date, time } = formatTimestamp(item.timestamp);
    const isCheckIn = item.type === 'CHECKIN';

    return (
      <View style={styles.itemCard}>
        <View
          style={[
            styles.typeIndicator,
            isCheckIn ? styles.checkInIndicator : styles.checkOutIndicator,
          ]}
        />
        <View style={styles.itemContent}>
          <Text style={styles.itemType}>{isCheckIn ? 'Check In' : 'Check Out'}</Text>
          <Text style={styles.itemTimestamp}>{`${date} at ${time}`}</Text>
        </View>
      </View>
    );
  };

  if (isLoading) {
    return (
      <View style={styles.centerContainer}>
        <ActivityIndicator size="large" color="#163A43" />
      </View>
    );
  }

  return (
    <SafeAreaView style={styles.safeArea}>
      <FlatList
        data={history}
        renderItem={renderItem}
        keyExtractor={(item) => item.id.toString()}
        style={styles.list}
        ListHeaderComponent={renderHeader}
        ListEmptyComponent={
          <View style={styles.centerContainer}>
            {error ? (
              <Text style={styles.errorText}>{error}</Text>
            ) : (
              <Text style={styles.emptyText}>No attendance history found.</Text>
            )}
          </View>
        }
        refreshControl={
          <RefreshControl refreshing={isRefreshing} onRefresh={onRefresh} />
        }
      />
    </SafeAreaView>
  );
};

// --- 6. Styles ---
const styles = StyleSheet.create({
  safeArea: {
    flex: 1,
    backgroundColor: '#F7F8FA',
  },
  list: {
    flex: 1,
  },
  centerContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 40,
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    color: '#163A43',
    paddingHorizontal: 20,
    marginTop: 10,
  },
  subtitle: {
    fontSize: 16,
    color: '#667',
    paddingHorizontal: 20,
    marginBottom: 20,
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingHorizontal: 20,
    marginBottom: 30,
  },
  button: {
    flex: 1,
    paddingVertical: 16,
    borderRadius: 12,
    alignItems: 'center',
    justifyContent: 'center',
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3,
  },
  checkInButton: {
    backgroundColor: '#10B981', // Emerald
    marginRight: 10,
  },
  checkOutButton: {
    backgroundColor: '#F59E0B', // Amber
    marginLeft: 10,
  },
  buttonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
  },
  historyTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#163A43',
    paddingHorizontal: 20,
    marginBottom: 10,
  },
  itemCard: {
    backgroundColor: '#fff',
    borderRadius: 10,
    marginHorizontal: 20,
    marginBottom: 10,
    flexDirection: 'row',
    alignItems: 'center',
    overflow: 'hidden',
    borderWidth: 1,
    borderColor: '#EFEFEF',
  },
  typeIndicator: {
    width: 6,
    height: '100%',
  },
  checkInIndicator: {
    backgroundColor: '#10B981',
  },
  checkOutIndicator: {
    backgroundColor: '#F59E0B',
  },
  itemContent: {
    padding: 16,
  },
  itemType: {
    fontSize: 16,
    fontWeight: '600',
    color: '#333',
  },
  itemTimestamp: {
    fontSize: 14,
    color: '#777',
    marginTop: 4,
  },
  emptyText: {
    fontSize: 16,
    color: '#888',
  },
  errorText: {
    fontSize: 16,
    color: '#D9534F',
    textAlign: 'center',
  },
});

export default Attendance;
