import React from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

// Sample data for the employees table
const employees = [
    { id: '001', name: 'John Doe', email: 'john.doe@example.com', role: 'Developer' },
    { id: '002', name: 'Jane Smith', email: 'jane.smith@example.com', role: 'Designer' },
    { id: '003', name: 'Sam Wilson', email: 'sam.wilson@example.com', role: 'Project Manager' },
    { id: '004', name: 'Alice Johnson', email: 'alice.j@example.com', role: 'QA Tester' },
];

function Dashboard() {
    const navigate = useNavigate();

    const handleLogout = async () => {
        try {
            await axios.post('http://localhost:8080/api/auth/logout', {}, { withCredentials: true });
            navigate('/login');
        } catch (error) {
            console.error('Logout failed:', error);
        }
    };

    return (
        <div className="min-h-screen bg-gray-900 text-white flex">
            {/* Sidebar */}
            <aside className="w-64 bg-gray-800 p-6">
                <h1 className="text-2xl font-bold mb-8">HRMS</h1>
                <nav>
                    <ul>
                        <li>
                            <a href="#employees" className="block py-2 px-4 text-lg rounded bg-blue-600">
                                Employees
                            </a>
                            <ul className="mt-2 ml-4">
                                <li>
                                    <a href="#new-employees" className="block py-1 px-2 text-gray-300 hover:bg-gray-700 rounded">
                                        New Employees
                                    </a>
                                </li>
                            </ul>
                        </li>
                        {/* Add other menu items here */}
                    </ul>
                </nav>
            </aside>

            {/* Main Content */}
            <main className="flex-1 flex flex-col">
                {/* Header */}
                <header className="bg-gray-800 shadow-md p-6 flex justify-between items-center">
                    <h2 className="text-3xl font-bold">Welcome to HR Management System</h2>
                    <button
                        onClick={handleLogout}
                        className="py-2 px-6 bg-red-600 font-semibold rounded-md hover:bg-red-700 transition duration-200"
                    >
                        Logout
                    </button>
                </header>

                {/* Content Area */}
                <div className="p-8">
                    <h3 className="text-2xl font-semibold mb-6">New Employees</h3>
                    <div className="bg-gray-800 rounded-lg shadow-xl overflow-hidden">
                        <table className="w-full text-left">
                            <thead className="bg-gray-700">
                            <tr>
                                <th className="p-4">Employee ID</th>
                                <th className="p-4">Name</th>
                                <th className="p-4">Email</th>
                                <th className="p-4">Role</th>
                            </tr>
                            </thead>
                            <tbody>
                            {employees.map((employee, index) => (
                                <tr key={employee.id} className={index % 2 === 0 ? 'bg-gray-800' : 'bg-gray-700'}>
                                    <td className="p-4">{employee.id}</td>
                                    <td className="p-4">{employee.name}</td>
                                    <td className="p-4">{employee.email}</td>
                                    <td className="p-4">{employee.role}</td>
                                </tr>
                            ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </main>
        </div>
    );
}

export default Dashboard;
