import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Payslip.css";

function Payslip() {
  const [employees, setEmployees] = useState([]);
  const [selectedEmployee, setSelectedEmployee] = useState("");
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:8080/api/employees/fetchall")
      .then((res) => setEmployees(res.data))
      .catch((err) => console.error(err));
  }, []);

  const handleUpload = () => {
    if (!selectedEmployee || !file) {
      setMessage("Please select an employee and a file.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    axios
      .post(
        `http://localhost:8080/api/payslips/upload/${selectedEmployee}`,
        formData,
        {
          headers: { "Content-Type": "multipart/form-data" },
        }
      )
      .then((res) => setMessage("Payslip uploaded successfully!"))
      .catch((err) => setMessage("Error uploading payslip."));
  };

  return (
    <div className="dashboard-container">
      <div className="main-content">
        <div className="header">
          <h1 className="header-title">Upload Payslip</h1>
        </div>
        <div className="content-area">
          {message && <div className="status-message">{message}</div>}
          <div className="payslip-form">
            <label>Select Employee:</label>
            <select
              value={selectedEmployee}
              onChange={(e) => setSelectedEmployee(e.target.value)}
            >
              <option value="">-- Select Employee --</option>
              {employees.map((emp) => (
                <option key={emp.id} value={emp.id}>
                  {emp.name}
                </option>
              ))}
            </select>

            <label>Select Payslip File:</label>
            <input
              type="file"
              onChange={(e) => setFile(e.target.files[0])}
            />

            <button className="approve-btn" onClick={handleUpload}>
              Upload Payslip
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Payslip;
