import React, { useEffect, useState } from "react";
import axios from "axios";
import "./LeaveRequest.css";

function LeaveRequest() {
  const [leaves, setLeaves] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // ✅ Fetch all leave requests when page loads
  useEffect(() => {
    const fetchLeaves = async () => {
      try {
        const response = await axios.get("http://localhost:8080/leave/all");
        setLeaves(response.data);
      } catch (err) {
        console.error(err);
        setError("Failed to load leave requests");
      } finally {
        setLoading(false);
      }
    };
    fetchLeaves();
  }, []);

  // ✅ Handle Approve / Reject
  const updateStatus = async (leaveId, status) => {
    try {
      await axios.put(
        `http://localhost:8080/leave/updateStatus/${leaveId}?status=${status}`
      );
      setLeaves((prev) =>
        prev.map((leave) =>
          leave.id === leaveId ? { ...leave, status: status.toUpperCase() } : leave
        )
      );
    } catch (err) {
      console.error(err);
      alert("Error updating leave status");
    }
  };

  if (loading) return <div className="loading">Loading leave requests...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="leave-page">
      <h2>All Leave Requests</h2>
      {leaves.length === 0 ? (
        <p>No leave requests found.</p>
      ) : (
        <table className="leave-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Employee Name</th>
              <th>Leave Date</th>
              <th>Reason</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {leaves.map((leave) => (
              <tr key={leave.id}>
                <td>{leave.id}</td>
                <td>{leave.employee?.name || "Unknown"}</td>
                <td>{leave.leaveDate}</td>
                <td>{leave.reason}</td>
                <td className={`status ${leave.status.toLowerCase()}`}>
                  {leave.status}
                </td>
                <td>
                  {leave.status === "PENDING" ? (
                    <>
                      <button
                        className="approve-btn"
                        onClick={() => updateStatus(leave.id, "APPROVED")}
                      >
                        Approve
                      </button>
                      <button
                        className="reject-btn"
                        onClick={() => updateStatus(leave.id, "REJECTED")}
                      >
                        Reject
                      </button>
                    </>
                  ) : (
                    <span>-</span>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default LeaveRequest;
