import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Attendance() {
  const [attendanceRecords, setAttendanceRecords] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Replace with the employeeId you want to fetch; for admin, you might loop over all employees instead
  const employeeId = "123";

  useEffect(() => {
    const fetchAttendance = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/attendance`, {
          params: { employeeId }
        });
        setAttendanceRecords(response.data);
      } catch (err) {
        console.error(err);
        setError('Failed to fetch attendance data.');
      } finally {
        setLoading(false);
      }
    };

    fetchAttendance();
  }, [employeeId]);

  if (loading) return <div>Loading attendance records...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h2>Attendance Records</h2>
      {attendanceRecords.length === 0 ? (
        <p>No records found.</p>
      ) : (
        <table style={{ borderCollapse: 'collapse', width: '100%' }}>
          <thead>
            <tr>
              <th style={thStyle}>Employee ID</th>
              <th style={thStyle}>Employee Name</th>
              <th style={thStyle}>Type</th>
              <th style={thStyle}>Note</th>
              <th style={thStyle}>Date</th>
            </tr>
          </thead>
          <tbody>
            {attendanceRecords.map((record) => (
              <tr key={record.id}>
                <td style={tdStyle}>{record.employeeId}</td>
                <td style={tdStyle}>{record.employeeName}</td>
                <td style={tdStyle}>{record.type}</td>
                <td style={tdStyle}>{record.note || '-'}</td>
                <td style={tdStyle}>{new Date(record.createdAt).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

const thStyle = {
  border: '1px solid #ddd',
  padding: '8px',
  backgroundColor: '#f2f2f2'
};

const tdStyle = {
  border: '1px solid #ddd',
  padding: '8px'
};

export default Attendance;
