import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Payslip() {
  const [employees, setEmployees] = useState([]);
  const [selectedEmployee, setSelectedEmployee] = useState('');
  const [file, setFile] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:8080/api/employees/fetchall')
      .then(res => setEmployees(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleUpload = () => {
    if (!selectedEmployee || !file) return alert('Select employee and file');

    const formData = new FormData();
    formData.append('file', file);

    axios.post(`http://localhost:8080/api/payslips/upload/${selectedEmployee}`, formData, {
      headers: { 'Content-Type': 'multipart/form-data' }
    })
      .then(res => alert('Payslip uploaded!'))
      .catch(err => console.error(err));
  };

  return (
    <div>
      <h2>Upload Payslip</h2>

      <select onChange={e => setSelectedEmployee(e.target.value)}>
        <option value="">Select Employee</option>
        {employees.map(emp => (
          <option key={emp.id} value={emp.id}>{emp.name}</option>
        ))}
      </select>

      <input type="file" onChange={e => setFile(e.target.files[0])} />
      <button onClick={handleUpload}>Upload</button>
    </div>
  );
}

export default Payslip;
