import React, { useState, useEffect } from "react";
import { 
  View, Text, TextInput, TouchableOpacity, StyleSheet, Alert, 
  ActivityIndicator, Platform, FlatList, SafeAreaView 
} from "react-native";
import DateTimePicker from "@react-native-community/datetimepicker";
import axios from "axios";
import AsyncStorage from '@react-native-async-storage/async-storage';

const API_URL = Platform.OS === 'ios' ? 'http://localhost:8080' : 'http://10.0.2.2:8080';

const RequestLeave = () => {
  const [date, setDate] = useState(new Date());
  const [showPicker, setShowPicker] = useState(false);
  const [reason, setReason] = useState("");
  const [employeeId, setEmployeeId] = useState(null);

  const [isPageLoading, setIsPageLoading] = useState(true);
  const [leaves, setLeaves] = useState([]);
  const [isRefreshing, setIsRefreshing] = useState(false);

  // --- Fetch employee ID from AsyncStorage and leave history
  useEffect(() => {
    const fetchEmployeeData = async () => {
      try {
        setIsPageLoading(true);
        const id = await AsyncStorage.getItem('@employee_id');
        if (id) {
          setEmployeeId(id);
          await fetchLeaveHistory(id);
        } else {
          Alert.alert("Error", "Employee ID not found. Please log in again.");
        }
      } catch (e) {
        console.error("Failed to load employee data", e);
        Alert.alert("Error", "Failed to load employee data.");
      } finally {
        setIsPageLoading(false);
      }
    };
    fetchEmployeeData();
  }, []);

  // --- Fetch leave history for employee
  const fetchLeaveHistory = async (id) => {
    if (!id) return;
    try {
      const response = await axios.get(`${API_URL}/leave/employee/${id}`);
      // Sort newest first
      setLeaves(response.data.sort((a, b) => new Date(b.leaveDate) - new Date(a.leaveDate)));
    } catch (error) {
      console.error("Failed to fetch leave history", error);
      Alert.alert("Error", "Could not load your leave history.");
    }
  };

  // --- Handle date picker
  const handleDateChange = (event, selectedDate) => {
    setShowPicker(Platform.OS === 'ios');
    if (selectedDate) setDate(selectedDate);
  };
  const showDatePicker = () => setShowPicker(true);

  // --- Submit leave
  const submitLeave = async () => {
    if (!employeeId) return Alert.alert("Error", "Employee data not loaded.");
    if (!reason.trim()) return Alert.alert("Error", "Please enter a reason.");

    try {
      await axios.post(`${API_URL}/leave/request/${employeeId}`, {
        leaveDate: date.toISOString().split("T")[0],
        reason,
      });
      Alert.alert("Success", "Leave requested successfully");
      setReason(""); 
      await fetchLeaveHistory(employeeId); // refresh list
    } catch (error) {
      Alert.alert("Error", "Failed to request leave. Check console.");
      if (error.response) {
        console.error("Server Error Data:", error.response.data);
        console.error("Server Error Status:", error.response.status);
      } else if (error.request) {
        console.error("Network Error:", error.request);
      } else {
        console.error("Axios Error:", error.message);
      }
    }
  };

  // --- Pull-to-refresh
  const onRefresh = async () => {
    setIsRefreshing(true);
    await fetchLeaveHistory(employeeId);
    setIsRefreshing(false);
  };

  // --- Render each leave item
  const LeaveItem = ({ item }) => (
    <View style={styles.leaveItem}>
      <View style={styles.leaveItemLeft}>
        <Text style={styles.leaveDate}>{item.leaveDate}</Text>
        <Text style={styles.leaveReason}>{item.reason}</Text>
      </View>
      <View style={[styles.statusBadge, styles[item.status]]}>
        <Text style={styles.statusText}>{item.status}</Text>
      </View>
    </View>
  );

  // --- Form header
  const renderFormHeader = () => (
    <View>
      <View style={styles.header}>
        <Text style={styles.title}>Request Leave</Text>
        <Text style={styles.subtitle}>Please enter your leave here</Text>
      </View>

      <TouchableOpacity style={styles.datePicker} onPress={showDatePicker}>
        <Text style={styles.dateText}>{date.toLocaleDateString()}</Text>
      </TouchableOpacity>

      {showPicker && (
        <DateTimePicker
          value={date}
          mode="date"
          display="default"
          onChange={handleDateChange}
        />
      )}

      <TextInput
        style={styles.input}
        placeholder="Reason for the Leave"
        value={reason}
        onChangeText={setReason}
        multiline
        placeholderTextColor="#888"
      />

      <TouchableOpacity style={styles.button} onPress={submitLeave}>
        <Text style={styles.buttonText}>Request Leave</Text>
      </TouchableOpacity>

      <Text style={styles.historyTitle}>Leave History</Text>
    </View>
  );

  // --- Show loading spinner
  if (isPageLoading) {
    return (
      <View style={[styles.container, { justifyContent: "center" }]}>
        <ActivityIndicator size="large" color="#163A43" />
        <Text style={{ textAlign: "center", marginTop: 10 }}>Loading...</Text>
      </View>
    );
  }

  // --- Main render ---
  return (
    <SafeAreaView style={styles.safeArea}>
      <FlatList
        data={leaves}
        renderItem={LeaveItem}
        keyExtractor={(item) => item.id.toString()}
        style={styles.list}
        contentContainerStyle={styles.listContent}
        ListHeaderComponent={renderFormHeader}
        ListEmptyComponent={<Text style={styles.emptyText}>You have no leave history.</Text>}
        refreshing={isRefreshing}
        onRefresh={onRefresh}
      />
    </SafeAreaView>
  );
};

export default RequestLeave;

// --- Styles ---
const styles = StyleSheet.create({
  safeArea: { flex: 1, backgroundColor: "#fff" },
  list: { flex: 1 },
  listContent: { padding: 20, paddingBottom: 60 },
  header: { backgroundColor: "#163A43", padding: 20, borderRadius: 15, marginBottom: 30 },
  title: { color: "#fff", fontSize: 22, fontWeight: "600" },
  subtitle: { color: "#B3C7CF", fontSize: 14 },
  datePicker: { backgroundColor: "#2F5D63", padding: 15, borderRadius: 10, marginBottom: 20, alignItems: 'center' },
  dateText: { color: "#fff", fontSize: 16 },
  input: { backgroundColor: "#f5f5f5", padding: 15, borderRadius: 10, height: 100, textAlignVertical: "top", fontSize: 16, color: '#333' },
  button: { backgroundColor: "#2F5D63", padding: 15, borderRadius: 30, alignItems: "center", marginTop: 30 },
  buttonText: { color: "#fff", fontWeight: "bold", fontSize: 16 },
  historyTitle: { fontSize: 20, fontWeight: '600', color: '#163A43', marginTop: 40, marginBottom: 20, borderTopWidth: 1, borderTopColor: '#eee', paddingTop: 20 },
  leaveItem: { backgroundColor: '#f9f9f9', borderRadius: 10, padding: 15, flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', marginBottom: 10, borderWidth: 1, borderColor: '#eee' },
  leaveItemLeft: { flex: 1 },
  leaveDate: { fontSize: 16, fontWeight: '600', color: '#333' },
  leaveReason: { fontSize: 14, color: '#666', marginTop: 4 },
  statusBadge: { paddingVertical: 5, paddingHorizontal: 10, borderRadius: 15 },
  statusText: { color: '#fff', fontSize: 12, fontWeight: '700' },
  PENDING: { backgroundColor: '#f59e0b' },
  APPROVED: { backgroundColor: '#10b981' },
  REJECTED: { backgroundColor: '#ef4444' },
  emptyText: { textAlign: 'center', marginTop: 20, fontSize: 16, color: '#888' },
  container: { flex: 1, padding: 20, backgroundColor: "#fff" },
});
