import React, { useState } from "react";
import TotalCount from "./TotalCount";
import "./Dashboard.css";

function Dashboard() {
  const [activeSection, setActiveSection] = useState("employeeOverview");

  const renderContent = () => {
    switch (activeSection) {
      case "employeeOverview":
        return <p>Employee Overview Content</p>;
      case "leaveManagement":
        return <p>Leave Management Content</p>;
      case "attendance":
        return <p>Attendance Content</p>;
      default:
        return <p>Select a section</p>;
    }
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <aside className="sidebar">
        <h2 className="sidebar-title">HRMS Dashboard</h2>
        <ul className="sidebar-menu">
          <li
            className={activeSection === "employeeOverview" ? "active" : ""}
            onClick={() => setActiveSection("employeeOverview")}
          >
            ðŸ‘¥ Employee Overview
          </li>
          <li
            className={activeSection === "leaveManagement" ? "active" : ""}
            onClick={() => setActiveSection("leaveManagement")}
          >
            ðŸ•’ Leave Management
          </li>
          <li
            className={activeSection === "attendance" ? "active" : ""}
            onClick={() => setActiveSection("attendance")}
          >
            ðŸ“… Attendance
          </li>
        </ul>
      </aside>

      {/* Main Content */}
      <main className="dashboard-main">
        <h1 className="dashboard-header">Welcome Back, Admin ðŸ‘‹</h1>

        {/* Total Count Section */}
        <TotalCount />

        {/* Section Content */}
        <div className="section-content">
          <h3 className="section-title">
            {activeSection === "employeeOverview"
              ? "Employee Overview"
              : activeSection === "leaveManagement"
              ? "Leave Management"
              : "Attendance"}
          </h3>
          {renderContent()}
        </div>
      </main>
    </div>
  );
}

export default Dashboard;
