import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Dashboard.css';

function Dashboard() {
    const [employees, setEmployees] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchEmployees = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/employees/getall');
                console.log("Data from backend:", response.data); // Keep this for debugging

                // Defensive Check: Ensure the response data is an array
                if (Array.isArray(response.data)) {
                    setEmployees(response.data);
                } else if (response.data && Array.isArray(response.data.content)) {
                    // This handles cases where the array is nested in a 'content' property
                    setEmployees(response.data.content);
                } else {
                    console.error("Received data is not an array:", response.data);
                    setEmployees([]); // Set to empty array to prevent crash
                }
            } catch (error) {
                console.error("There was an error fetching the employee data:", error);
            }
        };

        fetchEmployees();
    }, []);

    const handleLogout = async () => {
        // ... (logout logic is the same)
    };

    return (
        <div className="dashboard-container">
            {/* ... Sidebar and Header ... */}
            <main className="main-content">
                <header className="header">
                    <h2 className="header-title">Welcome to HR Management System</h2>
                    <button onClick={handleLogout} className="logout-button">Logout</button>
                </header>

                <div className="content-area">
                    <h3 className="content-title">New Employees</h3>
                    <div className="table-container">
                        <table className="employee-table">
                            <thead>
                            <tr>
                                <th>Employee ID</th>
                                <th>Name</th>
                                <th>Email</th>
                                <th>NIC</th>
                                <th>Gender</th>
                            </tr>
                            </thead>
                            <tbody>
                            { /* Add a check here as well for maximum safety */ }
                            {Array.isArray(employees) && employees.map((employee) => (
                                <tr key={employee.id}>
                                    <td>{employee.id}</td>
                                    <td>{employee.name}</td>
                                    <td>{employee.email}</td>

                                </tr>
                            ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </main>
        </div>
    );
}

export default Dashboard;
