import React, { useEffect, useState } from "react";
import axios from "axios";
import "./LeaveRequest.css";

function LeaveRequest() {
  const [leaves, setLeaves] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchLeaves();
  }, []);

  const fetchLeaves = async () => {
    try {
      const response = await axios.get("http://localhost:8080/leave/all");
      console.log("Leaves fetched:", response.data);
      setLeaves(response.data);
    } catch (err) {
      console.error(err);
      setError("Failed to load leave requests");
    } finally {
      setLoading(false);
    }
  };

  const updateStatus = async (leaveId, status) => {
    try {
      await axios.put(
        `http://localhost:8080/leave/updateStatus/${leaveId}?status=${status}`
      );
      setLeaves((prev) =>
        prev.map((leave) =>
          leave.id === leaveId ? { ...leave, status: status.toUpperCase() } : leave
        )
      );
    } catch (err) {
      console.error(err);
      alert("Error updating leave status");
    }
  };

  if (loading) return <p className="status-message">Loading leave requests...</p>;
  if (error) return <p className="status-message error">{error}</p>;
  if (leaves.length === 0) return <p className="status-message">No leave requests found.</p>;

  return (
    <div className="table-container">
      <table className="leave-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Employee Name</th>
            <th>Leave Date</th>
            <th>Reason</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {leaves.map((leave) => (
            <tr key={leave.id}>
              <td>{leave.id}</td>
              <td>{leave.employeeName}</td>
              <td>{leave.leaveDate}</td>
              <td>{leave.reason}</td>
              <td className={`status ${leave.status.toLowerCase()}`}>
                {leave.status}
              </td>
              <td>
                {leave.status === "PENDING" ? (
                  <>
                    <button onClick={() => updateStatus(leave.id, "APPROVED")}>Approve</button>
                    <button onClick={() => updateStatus(leave.id, "REJECTED")}>Reject</button>
                  </>
                ) : (
                  <span>-</span>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default LeaveRequest;
