import React, { useState, useEffect } from 'react';
import { useNavigate, NavLink } from 'react-router-dom';
import axios from 'axios';
import './Dashboard.css';

function Dashboard() {
    // --- STATE MANAGEMENT ---
    const [employees, setEmployees] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');
    const navigate = useNavigate();

    // --- DATA FETCHING ---
    useEffect(() => {
        const fetchEmployees = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/employees/getall', {
                    withCredentials: true,
                });
                if (Array.isArray(response.data)) {
                    setEmployees(response.data);
                } else {
                    throw new Error('Data received from server was not in the expected format.');
                }
            } catch (err) {
                console.error('There was an error fetching the employee data:', err);
                setError('Failed to load employee data. Please try again later.');
                if (err.response && (err.response.status === 401 || err.response.status === 403)) {
                    navigate('/login');
                }
            } finally {
                setLoading(false);
            }
        };

        fetchEmployees();
    }, [navigate]);

    // --- LOGOUT FUNCTIONALITY ---
    const handleLogout = async () => {
        try {
            await axios.post('http://localhost:8080/api/auth/logout', {}, {
                withCredentials: true,
            });
            navigate('/login');
        } catch (err) {
            console.error('Logout failed:', err);
            alert('Logout failed. Please try again.');
        }
    };

    // --- SEARCH FUNCTIONALITY ---
    const filteredEmployees = employees.filter(employee =>
        employee.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        employee.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
        employee.nic.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // --- CONDITIONAL RENDERING LOGIC ---
    const renderContent = () => {
        if (loading) {
            return (
                <div className="loading-container">
                    <div className="spinner"></div>
                    <p className="loading-text">Loading employees...</p>
                </div>
            );
        }

        if (error) {
            return (
                <div className="error-container">
                    <div className="error-icon">‚ö†Ô∏è</div>
                    <p className="error-text">{error}</p>
                </div>
            );
        }

        if (employees.length === 0) {
            return (
                <div className="empty-state">
                    <div className="empty-icon">üë•</div>
                    <h3>No employees found</h3>
                    <p>Start by adding your first employee to the system.</p>
                </div>
            );
        }

        return (
            <div className="table-section">
                <div className="table-header">
                    <div className="search-container">
                        <input
                            type="text"
                            placeholder="Search employees..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="search-input"
                        />
                    </div>
                    <div className="table-stats">
                        {filteredEmployees.length} of {employees.length} employees
                    </div>
                </div>

                <div className="table-container">
                    <table className="employee-table">
                        <thead>
                            <tr>
                                <th>Employee ID</th>
                                <th>Name</th>
                                <th>Email</th>
                                <th>NIC</th>
                                <th>Gender</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredEmployees.map((employee) => (
                                <tr key={employee.id}>
                                    <td>
                                        <span className="employee-id">#{employee.id}</span>
                                    </td>
                                    <td>
                                        <div className="employee-name">
                                            <div className="avatar">{employee.name.charAt(0)}</div>
                                            {employee.name}
                                        </div>
                                    </td>
                                    <td className="email-cell">{employee.email}</td>
                                    <td>{employee.nic}</td>
                                    <td>
                                        <span className={`gender-badge ${employee.gender.toLowerCase()}`}>
                                            {employee.gender}
                                        </span>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        );
    };

    return (
        <div className="dashboard-container">
            {/* Sidebar */}
            <aside className="sidebar">
                <div className="sidebar-header">
                    <div className="logo">
                        <div className="logo-icon">üè¢</div>
                        <span>HR Management</span>
                    </div>
                </div>

                <nav className="sidebar-nav">
                    <ul>
                        <li>
                            <NavLink
                                to="/dashboard"
                                className={({ isActive }) => `nav-item ${isActive ? 'active' : ''}`}
                            >
                                <span className="nav-icon">üìä</span>
                                Dashboard
                            </NavLink>
                        </li>
                        <li>
                            <NavLink
                                to="/Register"
                                className={({ isActive }) => `nav-item ${isActive ? 'active' : ''}`}
                            >
                                <span className="nav-icon">‚ûï</span>
                                Register Employee
                            </NavLink>
                        </li>
                        <li>
                            <NavLink
                                to="/attendance"
                                className={({ isActive }) => `nav-item ${isActive ? 'active' : ''}`}
                            >
                                <span className="nav-icon">üìÖ</span>
                                Attendance
                            </NavLink>
                        </li>
                        <li>
                            <NavLink
                                to="/leave-request"
                                className={({ isActive }) => `nav-item ${isActive ? 'active' : ''}`}
                            >
                                <span className="nav-icon">üèñÔ∏è</span>
                                Leave Request
                            </NavLink>
                        </li>
                    </ul>
                </nav>
            </aside>

            {/* Main Content */}
            <main className="main-content">
                <header className="header">
                    <div className="header-left">
                        <h1 className="header-title">Dashboard</h1>
                        <p className="header-subtitle">Manage your HR operations efficiently</p>
                    </div>
